if __name__ == '__main__':
    import argparse
    import hashlib
    # Print file's docstring if -h is invoked
    parser = argparse.ArgumentParser(description=__doc__, 
                formatter_class=argparse.RawDescriptionHelpFormatter)
    parser.add_argument(
        '--output-file-count', '-o', type=int, required=False,
        default=40,
        help='number of files in which to distribute kmers for running '
             'mhcnuggets')
    args = parser.parse_args()
    
    # Obtained from https://www.ncbi.nlm.nih.gov/nuccore/NC_045512
    covids = ['']
    kmers = []
    for i, covid in enumerate(covids):
        fhs = [open('covid_{}_{}.pep'.format(i, j),"w+")
                for j in range(args.output_file_count)]
        for kmer_size in [8, 9, 10, 11, 12]: #8-12mers for MHC-I binding
            kmers = set([covid[k:k+kmer_size]
                            for k in range(len(covid) - kmer_size + 1)])
            for kmer in kmers:
                print(kmer, file=fhs[int(
                                        hashlib.md5(kmer).hexdigest(), 16
                                     ) % args.output_file_count])
        for fh in fhs:
            fh.close()
