# SARS-CoV-2 ORF1a (YP_009725295.1) @ https://www.ncbi.nlm.nih.gov/protein/YP_009725295.1
ORF1a <- ""
# SARS-CoV-2 ORF1ab (YP_009724389.1) @ https://www.ncbi.nlm.nih.gov/protein/YP_009724389.1
ORF1ab <- ""
early.peptides <- SARS_CoV_2.kmers[unlist(lapply(SARS_CoV_2.kmers, function(x) { grepl(x, ORF1a) }))]
early2.peptides <- setdiff(SARS_CoV_2.kmers[unlist(lapply(SARS_CoV_2.kmers, function(x) { grepl(x, ORF1ab) }))], early.peptides)
late.peptides <- setdiff(SARS_CoV_2.kmers, union(early.peptides, early2.peptides))

# function to calculate number of early (ORF1a), early2 (ORF1b), or late peptides presented by a set of HLA alleles
allele.data.timed <- function(HLA=NULL, timing="early") {
	HLA <- HLA[which((HLA %in% MHC_alleles) & (!is.na(HLA)))]
	peptides <- character()
	for (hla in HLA) {
		peptides <- union(peptides, hash[[hla]])
	}
	peptides <- switch(timing,
					early = intersect(peptides, early.peptides),
					early2 = intersect(peptides, early2.peptides),
					late = intersect(peptides, late.peptides)
				)
	return(length(peptides))
}

#----------
# generate figure for paper
#----------
# Supplementary Figure S4
data <- data.frame(HLA=MHC_alleles, 
	early=unlist(lapply(MHC_alleles, allele.data.timed, timing="early"))*100/length(early.peptides),
	early2=unlist(lapply(MHC_alleles, allele.data.timed, timing="early2"))*100/length(early2.peptides),
	late=unlist(lapply(MHC_alleles, allele.data.timed, timing="late"))*100/length(late.peptides))
pdf(file=paste0(plotdir, "SupplementaryFigureS4.pdf"), width=6, height=6)
boxplot(data[,2:4], xlab="Relative time of SARS-CoV-2 peptide production", ylab="Presented SARS-CoV-2 peptides (%)", axes=FALSE)
axis(side=2, labels=TRUE)
axis(side=1, labels=c("Early\n(ORF1a)", "Early\n(ORF1b)", "Late\n(Subgenomic mRNAs)"), at=1:3, tick=FALSE)
box()
dev.off()